class Data extends Component {

  render() {
    const { person, load} = this.props;
    this.setState({
      name: person.name.first
    })
    const { name } = this.state

    return (
      <div>
        {load ? 'carregando...' 
        : <div>
          <p>{ name }</p>

        </div> }
      </div>
    )
  }
}

export default Data
