import React, { Component } from 'react'
import Data from './components/Data';

class App extends Component {
  state = {
    name: '',
    email: '',
    age: '',
  }

   fechApi = async () =>{
    const result = await fetch('https://api.randomuser.me/');
    const data = await result.json()
    const {results:{ name, email, age }} = data;

    this.setState({
      name: [name],
      email: [email],
      age: [age],
    })
  }

  componentDidMount () {
    this.fechApi();
  }
  render() {
    const { name, email, age } = this.state;
    return (
      <div>
        <Data name={name} email={email} age={age} />
      </div>
    )
  }
}

export default App;
